INSERT INTO `action_actionlibrary` VALUES ('228', '箭蹲提膝', '1、后大腿与地面垂直，膝盖不要着地\r\n2、脚尖与膝盖保持同一方向\r\n3、提膝至大腿略高于髋部', '1x20', null, null, null, '2', '1', '4', '289');
