INSERT INTO `action_actionlibrary` VALUES ('1160', '椅子深蹲', '1、缓缓下蹲，直到屁股轻轻碰到椅子即可还原，不要猛然放松坐到椅子上\r\n2、起身时自然放松', '1x20', null, null, null, '1', '1', '4', '1309');
