INSERT INTO `action_actionlibrary` VALUES ('1162', '揉四白穴', '1以左右食指与中指并拢，放在靠近鼻翼两侧，大拇指支撑在下腭骨凹陷处，然后放下中指，在面颊中央按揉。注意穴位不需移动，按揉面不要太大', '1x20', null, null, null, '1', '1', '17', '1311');
INSERT INTO `action_actionlibrary` VALUES ('1163', '按太阳穴 轮刮眼眶', '1起四指，以左右大拇指罗纹面按住太阳穴，以左右食指第二节内侧面轮刮眼眶上下一圈，上侧从眉头开始，到眉梢为止，下面从内眼角起至外眼角止，先上后下，轮刮上下一圈', '1x20', null, null, null, '1', '1', '17', '1312');
