INSERT INTO `action_actionlibrary` VALUES ('1167', '垫上合脚掌拉伸', '1于垫子上，双腿合掌并拢并且向两边打开，双腿可缓缓下压到大腿内侧有拉伸的感觉即可', '1x20', null, null, null, '1', '1', '3', '1316');
