INSERT INTO `action_actionlibrary` VALUES ('1172', 'V型触脚尖', '1部保持持续紧张，第一次起身时手指触碰脚尖。还原时顺势举腿，之后卷起再次触脚尖', '1x20', null, null, null, '2', '1', '21', '1321');
INSERT INTO `action_actionlibrary` VALUES ('1173', '探天应穴', '1左右大拇指罗纹面接左右眉头下面的上眶角处。其他四指散开弯曲如弓状，支在前额上，按探面不要大。', '1x20', null, null, null, '1', '1', '17', '1322');
INSERT INTO `action_actionlibrary` VALUES ('1174', '直腿举腿', '1伸直，动作中尽量保持腿部稳定。下落时控制腿运动，不要被惯性左右！', '1x20', null, null, null, '2', '1', '21', '1323');
